def give_member(arr, id):
    member_value = -1
    m = 0
    while m < len(arr):
        if m == id:
            member_value = arr[m]
        m += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    m = len(arr)
    while m > 0:
        m -= 1
        if m == id:
            member_value = arr[m]
    return member_value
