def give_member(arr, id):
    member_value = -1
    o = 0
    while o < len(arr):
        if o == id:
            member_value = arr[o]
        o += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    o = len(arr)
    while o > 0:
        o -= 1
        if o == id:
            member_value = arr[o]
    return member_value
