def give_member(arr, id):
    member_value = -1
    t = 0
    while t < len(arr):
        if t == id:
            member_value = arr[t]
        t += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    t = len(arr)
    while t > 0:
        t -= 1
        if t == id:
            member_value = arr[t]
    return member_value
