def find_min(arr):
    min_value = arr[0]
    e = 0
    while e < len(arr):
        if arr[e] < min_value:
            min_value = arr[e]
        e += 1
    return min_value

def find_min(arr):
    min_value = arr[-1]
    e = len(arr)
    while e > 0:
        e -= 1
        if arr[e] < min_value:
            min_value = arr[e]
    return min_value
