def find_min(arr):
    min_value = arr[0]
    h = 0
    while h < len(arr):
        if arr[h] < min_value:
            min_value = arr[h]
        h += 1
    return min_value

def find_min(arr):
    min_value = arr[-1]
    h = len(arr)
    while h > 0:
        h -= 1
        if arr[h] < min_value:
            min_value = arr[h]
    return min_value
