def give_member(arr, id):
    member_value = -1
    b = 0
    while b < len(arr):
        if b == id:
            member_value = arr[b]
        b += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    b = 0
    while b <= len(arr) - 1:
        if b == id:
            member_value = arr[b]
        b += 1
    return member_value
