def selection_sort(arr):
    m = 0
    while m < len(arr):
        z = m + 1
        while z < len(arr):
            if arr[z] < arr[m]:
                temp = arr[m]
                arr[m] = arr[z]
                arr[z] = temp
            z += 1
        m += 1
    return arr

def selection_sort(arr):
    m = 0
    while m <= len(arr) - 1:
        z = m + 1
        while z <= len(arr) - 1:
            if arr[z] < arr[m]:
                temp = arr[m]
                arr[m] = arr[z]
                arr[z] = temp
            z += 1
        m += 1
    return arr
