def find_min(arr):
    min_value = arr[0]
    j = 0
    while j < len(arr):
        if arr[j] < min_value:
            min_value = arr[j]
        j += 1
    return min_value

def find_min(arr):
    min_value = arr[0]
    j = 0
    while j <= len(arr) - 1:
        if arr[j] < min_value:
            min_value = arr[j]
        j += 1
    return min_value
