def find_min(arr):
    min_value = arr[0]
    o = 0
    while o < len(arr):
        if arr[o] < min_value:
            min_value = arr[o]
        o += 1
    return min_value

def find_min(arr):
    min_value = arr[0]
    o = 0
    while o <= len(arr) - 1:
        if arr[o] < min_value:
            min_value = arr[o]
        o += 1
    return min_value
