def find_min(arr):
    min_value = arr[0]
    s = 0
    while s < len(arr):
        if arr[s] < min_value:
            min_value = arr[s]
        s += 1
    return min_value

def find_min(arr):
    min_value = arr[0]
    s = 0
    while s <= len(arr) - 1:
        if arr[s] < min_value:
            min_value = arr[s]
        s += 1
    return min_value
