def selection_sort(arr):
    e = 0
    while e < len(arr):
        z = e + 1
        while z < len(arr):
            if arr[z] < arr[e]:
                temp = arr[e]
                arr[e] = arr[z]
                arr[z] = temp
            z += 1
        e += 1
    return arr

def selection_sort(arr):
    e = -1
    while e < len(arr) - 1:
        e += 1
        z = e + 1
        while z < len(arr):
            if arr[z] < arr[e]:
                temp = arr[e]
                arr[e] = arr[z]
                arr[z] = temp
            z += 1
    return arr
