def selection_sort(arr):
    g = 0
    while g < len(arr):
        z = g + 1
        while z < len(arr):
            if arr[z] < arr[g]:
                temp = arr[g]
                arr[g] = arr[z]
                arr[z] = temp
            z += 1
        g += 1
    return arr

def selection_sort(arr):
    g = -1
    while g < len(arr) - 1:
        g += 1
        z = g + 1
        while z < len(arr):
            if arr[z] < arr[g]:
                temp = arr[g]
                arr[g] = arr[z]
                arr[z] = temp
            z += 1
    return arr
