def selection_sort(arr):
    i = 0
    while i < len(arr):
        z = i + 1
        while z < len(arr):
            if arr[z] < arr[i]:
                temp = arr[i]
                arr[i] = arr[z]
                arr[z] = temp
            z += 1
        i += 1
    return arr

def selection_sort(arr):
    i = -1
    while i < len(arr) - 1:
        i += 1
        z = i + 1
        while z < len(arr):
            if arr[z] < arr[i]:
                temp = arr[i]
                arr[i] = arr[z]
                arr[z] = temp
            z += 1
    return arr
