def selection_sort(arr):
    j = 0
    while j < len(arr):
        z = j + 1
        while z < len(arr):
            if arr[z] < arr[j]:
                temp = arr[j]
                arr[j] = arr[z]
                arr[z] = temp
            z += 1
        j += 1
    return arr

def selection_sort(arr):
    j = -1
    while j < len(arr) - 1:
        j += 1
        z = j + 1
        while z < len(arr):
            if arr[z] < arr[j]:
                temp = arr[j]
                arr[j] = arr[z]
                arr[z] = temp
            z += 1
    return arr
