def selection_sort(arr):
    l = 0
    while l < len(arr):
        z = l + 1
        while z < len(arr):
            if arr[z] < arr[l]:
                temp = arr[l]
                arr[l] = arr[z]
                arr[z] = temp
            z += 1
        l += 1
    return arr

def selection_sort(arr):
    l = -1
    while l < len(arr) - 1:
        l += 1
        z = l + 1
        while z < len(arr):
            if arr[z] < arr[l]:
                temp = arr[l]
                arr[l] = arr[z]
                arr[z] = temp
            z += 1
    return arr
