def selection_sort(arr):
    t = 0
    while t < len(arr):
        z = t + 1
        while z < len(arr):
            if arr[z] < arr[t]:
                temp = arr[t]
                arr[t] = arr[z]
                arr[z] = temp
            z += 1
        t += 1
    return arr

def selection_sort(arr):
    t = -1
    while t < len(arr) - 1:
        t += 1
        z = t + 1
        while z < len(arr):
            if arr[z] < arr[t]:
                temp = arr[t]
                arr[t] = arr[z]
                arr[z] = temp
            z += 1
    return arr
