def find_min(arr):
    min_value = arr[0]
    c = 0
    while c < len(arr):
        if arr[c] < min_value:
            min_value = arr[c]
        c += 1
    return min_value

def find_min(arr):
    min_value = arr[0]
    c = -1
    while c < len(arr) - 1:
        c += 1
        if arr[c] < min_value:
            min_value = arr[c]
    return min_value
