def give_member(arr, id):
    member_value = -1
    q = 0
    while q < len(arr):
        if q == id:
            member_value = arr[q]
        q += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    q = len(arr)
    while q > 0:
        q -= 1
        if q == id:
            member_value = arr[q]
    return member_value
