def give_member(arr, id):
    member_value = -1
    r = 0
    while r < len(arr):
        if r == id:
            member_value = arr[r]
        r += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    r = len(arr)
    while r > 0:
        r -= 1
        if r == id:
            member_value = arr[r]
    return member_value
