def find_min(arr):
    min_value = arr[0]
    g = 0
    while g < len(arr):
        if arr[g] < min_value:
            min_value = arr[g]
        g += 1
    return min_value

def find_min(arr):
    min_value = arr[0]
    g = 0
    while g <= len(arr) - 1:
        if arr[g] < min_value:
            min_value = arr[g]
        g += 1
    return min_value
