def find_min(arr):
    min_value = arr[0]
    h = 0
    while h < len(arr):
        if arr[h] < min_value:
            min_value = arr[h]
        h += 1
    return min_value

def find_min(arr):
    min_value = arr[0]
    h = 0
    while h <= len(arr) - 1:
        if arr[h] < min_value:
            min_value = arr[h]
        h += 1
    return min_value
