def find_min(arr):
    min_value = arr[0]
    t = 0
    while t < len(arr):
        if arr[t] < min_value:
            min_value = arr[t]
        t += 1
    return min_value

def find_min(arr):
    min_value = arr[0]
    t = 0
    while t <= len(arr) - 1:
        if arr[t] < min_value:
            min_value = arr[t]
        t += 1
    return min_value
