def give_member(arr, id):
    member_value = -1
    d = 0
    while d < len(arr):
        if d == id:
            member_value = arr[d]
        d += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    d = -1
    while d < len(arr) - 1:
        d += 1
        if d == id:
            member_value = arr[d]
    return member_value
