def selection_sort(arr):
    b = 0
    while b < len(arr):
        z = b + 1
        while z < len(arr):
            if arr[z] < arr[b]:
                temp = arr[b]
                arr[b] = arr[z]
                arr[z] = temp
            z += 1
        b += 1
    return arr

def selection_sort(arr):
    b = -1
    while b < len(arr) - 1:
        b += 1
        z = b + 1
        while z < len(arr):
            if arr[z] < arr[b]:
                temp = arr[b]
                arr[b] = arr[z]
                arr[z] = temp
            z += 1
    return arr
