def give_count(arr, target):
    count_value = 0
    m = 0
    while m < len(arr):
        if arr[m] == target:
            count_value += 1
        m += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    m = -1
    while m < len(arr) - 1:
        m += 1
        if arr[m] == target:
            count_value += 1
    return count_value
