def give_member(arr, id):
    member_value = -1
    e = 0
    while e < len(arr):
        if e == id:
            member_value = arr[e]
        e += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    e = len(arr)
    while e > 0:
        e -= 1
        if e == id:
            member_value = arr[e]
    return member_value
