def give_member(arr, id):
    member_value = -1
    f = 0
    while f < len(arr):
        if f == id:
            member_value = arr[f]
        f += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    f = len(arr)
    while f > 0:
        f -= 1
        if f == id:
            member_value = arr[f]
    return member_value
