def give_member(arr, id):
    member_value = -1
    n = 0
    while n < len(arr):
        if n == id:
            member_value = arr[n]
        n += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    n = len(arr)
    while n > 0:
        n -= 1
        if n == id:
            member_value = arr[n]
    return member_value
