def give_member(arr, id):
    member_value = -1
    p = 0
    while p < len(arr):
        if p == id:
            member_value = arr[p]
        p += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    p = len(arr)
    while p > 0:
        p -= 1
        if p == id:
            member_value = arr[p]
    return member_value
