    return sum_value'}, {'role': 'assistant', 'content': 'sum_value = sum(arr[0]->arr[k-1])'}])

def selection_sort(arr):
    c = 0
    while c < len(arr):
        z = c + 1
        while z < len(arr):
            if arr[z] < arr[c]:
                temp = arr[c]
                arr[c] = arr[z]
                arr[z] = temp
            z += 1
        c += 1
    return arr

