def find_min(arr):
    min_value = arr[0]
    d = 0
    while d < len(arr):
        if arr[d] < min_value:
            min_value = arr[d]
        d += 1
    return min_value

def find_min(arr):
    min_value = arr[-1]
    d = len(arr)
    while d > 0:
        d -= 1
        if arr[d] < min_value:
            min_value = arr[d]
    return min_value
