def selection_sort(arr):
    s = 0
    while s < len(arr):
        z = s + 1
        while z < len(arr):
            if arr[z] < arr[s]:
                temp = arr[s]
                arr[s] = arr[z]
                arr[z] = temp
            z += 1
        s += 1
    return arr

def selection_sort(arr):
    s = 0
    while s <= len(arr) - 1:
        z = s + 1
        while z <= len(arr) - 1:
            if arr[z] < arr[s]:
                temp = arr[s]
                arr[s] = arr[z]
                arr[z] = temp
            z += 1
        s += 1
    return arr
