def find_min(arr):
    min_value = arr[0]
    i = 0
    while i < len(arr):
        if arr[i] < min_value:
            min_value = arr[i]
        i += 1
    return min_value

def find_min(arr):
    min_value = arr[0]
    i = 0
    while i <= len(arr) - 1:
        if arr[i] < min_value:
            min_value = arr[i]
        i += 1
    return min_value
