def find_min(arr):
    min_value = arr[0]
    k = 0
    while k < len(arr):
        if arr[k] < min_value:
            min_value = arr[k]
        k += 1
    return min_value

def find_min(arr):
    min_value = arr[0]
    k = 0
    while k <= len(arr) - 1:
        if arr[k] < min_value:
            min_value = arr[k]
        k += 1
    return min_value
