def give_member(arr, id):
    member_value = -1
    s = 0
    while s < len(arr):
        if s == id:
            member_value = arr[s]
        s += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    s = -1
    while s < len(arr) - 1:
        s += 1
        if s == id:
            member_value = arr[s]
    return member_value
