def give_count(arr, target):
    count_value = 0
    b = 0
    while b < len(arr):
        if arr[b] == target:
            count_value += 1
        b += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    b = -1
    while b < len(arr) - 1:
        b += 1
        if arr[b] == target:
            count_value += 1
    return count_value
