def give_count(arr, target):
    count_value = 0
    f = 0
    while f < len(arr):
        if arr[f] == target:
            count_value += 1
        f += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    f = -1
    while f < len(arr) - 1:
        f += 1
        if arr[f] == target:
            count_value += 1
    return count_value
