def give_member(arr, id):
    member_value = -1
    b = 0
    while b < len(arr):
        if b == id:
            member_value = arr[b]
        b += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    b = len(arr)
    while b > 0:
        b -= 1
        if b == id:
            member_value = arr[b]
    return member_value
