def find_min(arr):
    min_value = arr[0]
    m = 0
    while m < len(arr):
        if arr[m] < min_value:
            min_value = arr[m]
        m += 1
    return min_value

def find_min(arr):
    min_value = arr[0]
    m = -1
    while m < len(arr) - 1:
        m += 1
        if arr[m] < min_value:
            min_value = arr[m]
    return min_value
