def find_max(arr):
    max_value = arr[0]
    r = 0
    while r < len(arr):
        if arr[r] > max_value:
            max_value = arr[r]
        r += 1
    return max_value

def find_max(arr):
    max_value = arr[-1]
    r = len(arr)
    while r > 0:
        r -= 1
        if arr[r] > max_value:
            max_value = arr[r]
    return max_value
