def give_count(arr, target):
    count_value = 0
    i = 0
    while i < len(arr):
        if arr[i] == target:
            count_value += 1
        i += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    i = len(arr)
    while i > 0:
        i -= 1
        if arr[i] == target:
            count_value += 1
    return count_value
