def find_max(arr):
    max_value = arr[0]
    l = 0
    while l < len(arr):
        if arr[l] > max_value:
            max_value = arr[l]
        l += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    l = 0
    while l <= len(arr) - 1:
        if arr[l] > max_value:
            max_value = arr[l]
        l += 1
    return max_value
