def find_max(arr):
    max_value = arr[0]
    m = 0
    while m < len(arr):
        if arr[m] > max_value:
            max_value = arr[m]
        m += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    m = -1
    while m < len(arr) - 1:
        m += 1
        if arr[m] > max_value:
            max_value = arr[m]
    return max_value
