def find_min(arr):
    min_value = arr[0]
    p = 0
    while p < len(arr):
        if arr[p] < min_value:
            min_value = arr[p]
        p += 1
    return min_value

def find_min(arr):
    min_value = arr[0]
    p = -1
    while p < len(arr) - 1:
        p += 1
        if arr[p] < min_value:
            min_value = arr[p]
    return min_value
