def find_max(arr):
    max_value = arr[0]
    f = 0
    while f < len(arr):
        if arr[f] > max_value:
            max_value = arr[f]
        f += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    f = -1
    while f < len(arr) - 1:
        f += 1
        if arr[f] > max_value:
            max_value = arr[f]
    return max_value
