def give_member(arr, id):
    member_value = -1
    l = 0
    while l < len(arr):
        if l == id:
            member_value = arr[l]
        l += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    l = len(arr)
    while l > 0:
        l -= 1
        if l == id:
            member_value = arr[l]
    return member_value
