def find_min(arr):
    min_value = arr[0]
    f = 0
    while f < len(arr):
        if arr[f] < min_value:
            min_value = arr[f]
        f += 1
    return min_value

def find_min(arr):
    min_value = arr[-1]
    f = len(arr)
    while f > 0:
        f -= 1
        if arr[f] < min_value:
            min_value = arr[f]
    return min_value
