def find_min(arr):
    min_value = arr[0]
    s = 0
    while s < len(arr):
        if arr[s] < min_value:
            min_value = arr[s]
        s += 1
    return min_value

def find_min(arr):
    min_value = arr[-1]
    s = len(arr)
    while s > 0:
        s -= 1
        if arr[s] < min_value:
            min_value = arr[s]
    return min_value
