def give_count(arr, target):
    count_value = 0
    q = 0
    while q < len(arr):
        if arr[q] == target:
            count_value += 1
        q += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    q = len(arr)
    while q > 0:
        q -= 1
        if arr[q] == target:
            count_value += 1
    return count_value
