def find_max(arr):
    max_value = arr[0]
    s = 0
    while s < len(arr):
        if arr[s] > max_value:
            max_value = arr[s]
        s += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    s = -1
    while s < len(arr) - 1:
        s += 1
        if arr[s] > max_value:
            max_value = arr[s]
    return max_value
